const items = $input.all();

// Coach name to email mapping
function getCoachEmail(coachName) {
  const coachMapping = {
    'Ain': 'ain.nizar@storehub.com',
    'Andrea': 'andrea.kaur@storehub.com',
    'Elaine': 'elaine.pee@storehub.com',
    'William': 'william.yew@storehub.com',
    'Izzuddin': 'izzuddin.azizol@storehub.com'
  };
  return coachMapping[coachName] || 'andrea.kaur@storehub.com'; // Default fallback
}

// Assessment schedule mapping based on your configuration
const assessmentSchedule = {
  'Merchant Care': [
    { 
      name: 'Hardware Assessment', 
      day: 3, 
      formUrl: 'https://forms.gle/4uqyPthsVweeZPqx6', 
      instructions: 'Demonstrate basic troubleshooting based on scenario' 
    },
    { 
      name: 'Care Mock Test', 
      day: 4, 
      formUrl: 'https://forms.gle/FKaQMgkzC6waDdhh8', 
      instructions: 'Assess product knowledge and soft skills when handling merchants' 
    }
  ],
  'Merchant Onboarding Manager': [
    { 
      name: 'Hardware Assessment', 
      day: 3, 
      formUrl: 'https://forms.gle/4uqyPthsVweeZPqx6', 
      instructions: 'Demonstrate basic troubleshooting based on scenario' 
    }
  ]
};

const scheduledAssessments = [];

items.forEach(item => {
  const trainee = item.json;
  const role = trainee.Department;
  const daysTraining = parseInt(trainee['Days since Training Start ']);
  const assessments = assessmentSchedule[role] || [];

  // Find assessments due today
  const dueToday = assessments.filter(assessment => assessment.day === daysTraining);

  dueToday.forEach(assessment => {
    scheduledAssessments.push({
      json: {
        trainee_name: trainee['Full Name'],
        trainee_email: trainee['Email Address'],
        coach_email: getCoachEmail(trainee['Coach Name']),
        role: role,
        assessment_name: assessment.name,
        form_url: assessment.formUrl,
        instructions: assessment.instructions,
        training_day: daysTraining
      }
    });
  });
});

return scheduledAssessments;

