// ===== WIN 3: SMART REMINDER LOGIC - WITH DYNAMIC COACH LOOKUP =====

console.log('=== STARTING SMART REMINDER LOGIC ===');

// ========================================
// STEP 1: GET COACH DIRECTORY FOR DYNAMIC EMAIL LOOKUP
// ========================================

const coachDirectory = $('Get Coach Directory').all();
console.log(`Loaded ${coachDirectory.length} coach directory entries`);

// Build coach email map from directory
function getCoachEmail(coachName) {
  if (!coachName) {
    console.log('âš ï¸ No coach name provided, defaulting to Andrea');
    return 'andrea.kaur@storehub.com';
  }

  // Clean the coach name (trim whitespace)
  const cleanName = coachName.trim();

  // Look up in coach directory
  const coach = coachDirectory.find(c => {
    const dirCoachName = c.json['Coach Name']?.trim();
    return dirCoachName && dirCoachName.toLowerCase() === cleanName.toLowerCase();
  });

  if (coach && coach.json['Coach Email']) {
    console.log(`âœ… Found email for ${cleanName}: ${coach.json['Coach Email']}`);
    return coach.json['Coach Email'];
  }

  // Fallback: construct email from name
  console.log(`âš ï¸ Coach ${cleanName} not found in directory, using fallback`);
  const emailName = cleanName.toLowerCase().replace(/\s+/g, '.');
  return `${emailName}@storehub.com`;
}

// ========================================
// STEP 2: GET INPUT DATA (TRAINEES WITH INCOMPLETE ASSESSMENTS)
// ========================================

let incompleteTrainees = [];

try {
  const inputData = $input.all();
  console.log('Number of inputs:', inputData.length);

  if (inputData.length > 0) {
    const rawData = inputData[0].json;
    console.log('Raw data type:', typeof rawData);
    console.log('Is array?', Array.isArray(rawData));

    if (Array.isArray(rawData)) {
      incompleteTrainees = rawData;
    } else {
      incompleteTrainees = [rawData];
    }
  }

  console.log('Total trainees processed:', incompleteTrainees.length);
} catch (error) {
  console.log('Error processing input:', error);
  return [];
}

const remindersToSend = [];

// ========================================
// STEP 3: PROCESS EACH TRAINEE WITH INCOMPLETE ASSESSMENTS
// ========================================

incompleteTrainees.forEach((trainee, index) => {
  console.log(`\nProcessing trainee ${index + 1}: ${trainee['Full Name']}`);

  const reminderCount = parseInt(trainee['Reminder Count']) || 0;
  const lastReminderSent = trainee['Last Reminder Sent'];
  const daysSinceTrainingStart = parseInt(trainee['Days since Training Start ']) || 0;
  const incompleteCount = parseInt(trainee['Total Assessments Incomplete']) || 0;

  console.log(`- Reminder count: ${reminderCount}`);
  console.log(`- Days since training: ${daysSinceTrainingStart}`);
  console.log(`- Incomplete assessments: ${incompleteCount}`);

  let shouldSendReminder = false;

  // REMINDER LOGIC:
  // 1. First reminder: Always send if incomplete > 0 and days > 1
  // 2. Subsequent reminders: Only after 3 days gap, max 2 total

  if (reminderCount === 0) {
    // First reminder
    if (incompleteCount > 0 && daysSinceTrainingStart > 1) {
      shouldSendReminder = true;
      console.log('â†’ First reminder - sending');
    }
  } else if (reminderCount < 2) {
    // Subsequent reminders (max 2 total)
    if (lastReminderSent) {
      try {
        const lastReminderDate = new Date(lastReminderSent);
        const daysSinceLastReminder = Math.floor((new Date() - lastReminderDate) / (1000 * 60 * 60 * 24));
        console.log(`- Days since last reminder: ${daysSinceLastReminder}`);

        if (daysSinceLastReminder >= 3) {
          shouldSendReminder = true;
          console.log('â†’ Subsequent reminder - sending (3+ days gap)');
        } else {
          console.log('â†’ Skipping (too recent)');
        }
      } catch (error) {
        console.log('â†’ Error parsing last reminder date, sending reminder');
        shouldSendReminder = true;
      }
    } else {
      // No last reminder date, send it
      shouldSendReminder = true;
      console.log('â†’ No last reminder date - sending');
    }
  } else {
    console.log('â†’ Maximum reminders reached (2)');
  }

  if (shouldSendReminder) {
    remindersToSend.push({
      trainee_name: trainee['Full Name'],
      trainee_email: trainee['Email Address'],
      coach_name: trainee['Coach Name'],
      coach_email: getCoachEmail(trainee['Coach Name']),
      role: trainee.Department,
      country: trainee.Country,  // â† ADDED COUNTRY FIELD
      incomplete_count: incompleteCount,
      days_training: daysSinceTrainingStart,
      current_reminder_count: reminderCount,
      new_reminder_count: reminderCount + 1,
      reminder_date: new Date().toISOString(),
      total_required: parseInt(trainee['Total Assessments Required']) || 0,
      total_completed: parseInt(trainee['Total Assessments Completed']) || 0
    });
  }
});

// ========================================
// STEP 4: RETURN RESULTS
// ========================================

console.log(`\n=== REMINDER SUMMARY ===`);
console.log(`Total trainees processed: ${incompleteTrainees.length}`);
console.log(`Reminders to send: ${remindersToSend.length}`);

if (remindersToSend.length === 0) {
  console.log('No reminders needed at this time');
  return [];
}

console.log('Reminders being sent to:', remindersToSend.map(r => `${r.trainee_name} (${r.country}) â†’ ${r.coach_email}`));

return remindersToSend;

// ===== WIN 3: GROUP REMINDERS BY ROLE + COUNTRY =====

console.log('=== STARTING REMINDER GROUPING ===');

// Get reminders from Smart Reminder Logic
const reminders = $input.all().map(item => item.json);
console.log(`Processing ${reminders.length} reminders`);

// Get coach directory for lookups
const coachDirectory = $('Get Coach Directory').all();
console.log(`Loaded ${coachDirectory.length} coach directory entries`);

// Group reminders by Role + Country
const groups = new Map();

reminders.forEach(reminder => {
  const key = `${reminder.role}_${reminder.country || 'Unknown'}`;

  if (!groups.has(key)) {
    groups.set(key, {
      role: reminder.role,
      country: reminder.country || 'Unknown',
      trainees: [],
      total_incomplete: 0
    });
  }

  const group = groups.get(key);
  group.trainees.push({
    name: reminder.trainee_name,
    email: reminder.trainee_email,
    incomplete_count: reminder.incomplete_count,
    days_training: reminder.days_training,
    total_required: reminder.total_required,
    total_completed: reminder.total_completed,
    coach_name: reminder.coach_name,
    // ADD REMINDER TRACKING FIELDS:
    reminder_date: reminder.reminder_date,
    new_reminder_count: reminder.new_reminder_count,
    current_reminder_count: reminder.current_reminder_count
  });
  group.total_incomplete += reminder.incomplete_count;
});

console.log(`Created ${groups.size} Role+Country groups`);

// For each group, get ALL coaches for that Role+Country
const results = [];

groups.forEach((group, key) => {
  console.log(`\nProcessing group: ${group.role} (${group.country})`);

  // Find all coaches for this Role+Country
  const coaches = coachDirectory.filter(c => {
    const matchRole = c.json['Role'] === group.role;
    const matchCountry = c.json['Country'] === group.country;
    return matchRole && matchCountry && c.json['Coach Email'];
  });

  // Get unique coach emails
  const coachEmails = [...new Set(coaches.map(c => c.json['Coach Email']))];
  const coachNames = [...new Set(coaches.map(c => c.json['Coach Name']))];

  console.log(`Found ${coachEmails.length} coaches:`, coachEmails);

  if (coachEmails.length === 0) {
    console.log('âš ï¸ No coaches found, defaulting to Andrea');
    coachEmails.push('andrea.kaur@storehub.com');
    coachNames.push('Andrea');
  }

  // Create trainee list for email
  const traineeList = group.trainees.map((t, idx) => 
    `${idx + 1}. ${t.name} - ${t.incomplete_count} incomplete (Day ${t.days_training}) [Coach: ${t.coach_name}]`
  ).join('\n');

  results.push({
    role: group.role,
    country: group.country,
    coach_emails: coachEmails.join(', '),
    coach_names: coachNames.join(', '),
    total_trainees: group.trainees.length,
    total_incomplete: group.total_incomplete,
    trainee_list: traineeList,
    trainees_data: group.trainees // This now includes reminder tracking fields
  });
});

console.log(`\n=== GROUPING COMPLETE ===`);
console.log(`Total groups to notify: ${results.length}`);

return results;

// Get the current group's data from the input (coming from Send a message)
// We need to reference back to the Group node's output for this execution item
const groupData = $input.first().json;

// Check if trainees_data exists
if (!groupData.trainees_data || groupData.trainees_data.length === 0) {
  console.log('No trainees_data found, trying to get from Group node');
  // Fallback: try to get from the pairedItem reference
  const groupNodeData = $('Group Reminders by Role+Country').item.json;
  return groupNodeData.trainees_data.map(trainee => ({
    json: trainee
  }));
}

console.log(`Extracting ${groupData.trainees_data.length} trainees for sheet update`);

// Return each trainee as a separate item for sheet update
return groupData.trainees_data.map(trainee => ({
  json: trainee
}));
